const Slides=document.querySelectorAll(".slider__item--reviews"),Videos=document.querySelectorAll(".slider__item--reviews video"),playButton=document.querySelector(".slider__play-button"),Slider=document.querySelector(".reviews__slider"),buttonNext=Slider.querySelector(".slider-controls__button--next"),buttonPrev=Slider.querySelector(".slider-controls__button--prev"),paginationItems=Slider.querySelectorAll(".slider__pagination-item");playButton.classList.remove("slider__play-button--hidden");for(let e of Videos)e.removeAttribute("controls");playButton.addEventListener("click",(function(){for(let e=0;e<Slides.length;e++)Slides[e].classList.contains("slider__item--active")&&(Videos[e].play(),playButton.classList.add("slider__play-button--hidden"))}));for(let e=0;e<Slides.length;e++)Slides[e].addEventListener("click",(function(){Slides[e].classList.contains("slider__item--active")&&(Videos[e].pause(),playButton.classList.remove("slider__play-button--hidden"))}));buttonNext.addEventListener("click",pauseVideoAll),buttonPrev.addEventListener("click",pauseVideoAll);for(let e of paginationItems)e.addEventListener("click",pauseVideoAll);for(let e=0;e<Slides.length;e++)Videos[e].addEventListener("ended",(function(){Videos[e].load(),playButton.classList.remove("slider__play-button--hidden")}));function pauseVideoAll(){for(let e of Videos)e.pause(),playButton.classList.remove("slider__play-button--hidden")}